// Burger
let headerBurger = document.querySelector('.header__burger');
let headerMenu = document.querySelector('.header__menu');
let mainBody = document.body;
headerBurger.addEventListener('click', function () {
    headerBurger.classList.toggle('active');
    headerMenu.classList.toggle('active');
    mainBody.classList.toggle('overflow');
})

// Burger


// slider-swiper

let count = 0;
let width;
let sliderLine = document.querySelector('.slider-swiper__line');
let sliderItems = document.querySelectorAll('.slider-item');
let arrowRight = document.querySelector('.button-item__btn-right');
let arrowLeft = document.querySelector('.button-item__btn-left');
let sliderWrapper = document.querySelector('.slider-swiper__wrapper');

function init() {
    if (sliderWrapper) {
        width = sliderWrapper.offsetWidth;
    }
    if (sliderLine) {
        sliderLine.style.width = width + sliderItems.length + 'px';
    }
    sliderItems.forEach(item => {
        item.style.width = width + 'px';
        item.style.height = 'auto';
    });
    rollSlider();

};


if (arrowLeft) {
    arrowLeft.addEventListener('click', function () {

        count--;
        if (count < 0) {
            count = sliderItems.length - 1;
        }

        rollSlider();

    });
};


if (arrowRight) {
    arrowRight.addEventListener('click', function () {
        count++;
        if (count >= sliderItems.length) {
            count = 0;
        }

        rollSlider();

    });
};

function rollSlider() {
    if (sliderLine) {
        sliderLine.style.transform = 'translate(-' + count * width + 'px)';
    }

}
window.addEventListener('resize', init);

init();
// slider-swiper

// slider-school

function firstSlider() {
    let sliderItem = document.querySelectorAll(".slider-school__slide-item");
    let sliderLeft = document.querySelector('.arrow-next-one');
    let sliderRight = document.querySelector('.arrow-prev-one');
    let index = 0;

    const activeSlide = n => {
        for (slide of sliderItem) {
            slide.classList.remove('active');
        }
        sliderItem[n].classList.add('active');
    }

    let nextSlide = () => {
        if (index == sliderItem.length - 1) {
            index = 0;
            activeSlide(index);
        } else {
            index++;
            activeSlide(index);
        }
    }
    let prevSlide = () => {
        if (index == 0) {
            index = sliderItem.length - 1;
            activeSlide(index);
        } else {
            index--;
            activeSlide(index);
        }
    }
    if (sliderLeft) {
        sliderLeft.addEventListener('click', nextSlide)
    }
    if (sliderRight) {
        sliderRight.addEventListener('click', prevSlide)
    }


}
firstSlider();

