function firstSlider() {
    let sliderItem = document.querySelectorAll(".slider-school__slide-item");
    let sliderLeft = document.querySelector('.arrow-next-one');
    let sliderRight = document.querySelector('.arrow-prev-one');
    let index = 0;

    const activeSlide = n => {
        for (slide of sliderItem) {
            slide.classList.remove('active');
        }
        sliderItem[n].classList.add('active');
    }

    let nextSlide = () => {
        if (index == sliderItem.length - 1) {
            index = 0;
            activeSlide(index);
        } else {
            index++;
            activeSlide(index);
        }
    }
    let prevSlide = () => {
        if (index == 0) {
            index = sliderItem.length - 1;
            activeSlide(index);
        } else {
            index--;
            activeSlide(index);
        }
    }
    sliderLeft.addEventListener('click', nextSlide)
    sliderRight.addEventListener('click', prevSlide)
}
firstSlider();

